OPTIONAL { ?justification a aida:TextJustification . ?justification aida:startOffset ?so . ?justification aida:endOffsetInclusive ?eo }
OPTIONAL { ?justification a aida:ImageJustification . ?justification aida:boundingBox ?bb . ?bb aida:boundingBoxUpperLeftX ?ulx . ?bb aida:boundingBoxUpperLeftY ?uly . ?bb aida:boundingBoxLowerRightX ?brx . ?bb aida:boundingBoxLowerRightY ?bry }
